mapper.select:
SELECT bno, title, content, writer
, view_cnt, comment_cnt, reg_date
FROM board

    WHERE bno = #{bno}


    ORDER BY reg_date DESC, bno DESC
    LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE board
SET   title = #{title}
  , content = #{content}
  , up_date = now()
WHERE bno = #{bno}

UPDATE board
SET   view_cnt = view_cnt + 1
WHERE bno = #{bno}
